///////// SVG CODE //////////
/////////////////////////////


function display_svg($type, $width=null, $height=null) {
	switch($type) {
		case 'search':
			echo svg_search($width, $height);
			return;
		case 'twitter':
			echo svg_twitter($width, $height);
			return;
		case 'facebook':
			echo svg_facebook($width, $height);
			return;
		// case 'twitter_large':
		// 	echo svg_twitter_large($width, $height);
		// 	return;
		case 'youtube':
			echo svg_youtube($width, $height);
			return;
		case 'linkedin':
			echo svg_linkedin($width, $height);
			return;
		case 'vimeo':
			echo svg_vimeo($width, $height);
			return;
		case 'storify':
			echo svg_storify($width, $height);
			return;
		case 'instagram':
			echo svg_instagram($width, $height);
			return;
		case 'instagram':
		default:
			echo '<!-- SVG NOT FOUND -->';
			return;
	}
}


function get_svg_ratio_height($default_width, $new_width, $default_height=null) {

	$scale = $new_width / $default_width;

	if( $default_height ) {
		return $default_height * $scale;
	}

	return $default_width * $scale;

}

